/**
 * @file
 */

module.exports = function (grunt) {

  // Project configuration.
  grunt.initConfig({
    compass: {
      dist: {
        options: {
          config: 'config.rb',
          force: true
        }
      }
    },
    pixrem: {
      options: {
        rootvalue: '16px'
      },
      dist: {
        src: 'css/styles.css',
        dest: 'css/styles.css'
      }
    },
    // http://blesscss.com/
    bless: {
      css: {
        options: {
          compress: true,
          force: true
        },
        files: {
          'css/styles.css': 'css/styles.css'
        }
      }
    }
  });

  // These plugins provide necessary tasks.
  grunt.loadNpmTasks('grunt-contrib-compass');
  grunt.loadNpmTasks('grunt-pixrem');
  grunt.loadNpmTasks('grunt-bless');

  // Default task.
  grunt.registerTask('default', ['compass', 'pixrem', 'bless']);

};
