<?php

/**
 * @file
 * Fieldable panel pane viewmode template for card overlay component.
 */

if ($href):
  print '<a class="card__primary-link" href="' . $href . '">';
endif
?>

  <div <?php print drupal_attributes($attributes_array); ?>
    class="<?php print implode(" ", $classes_array); ?> card card--background-image"
    style="background-image: url('<?php print $image; ?>')">

    <div class="card__header"></div>
    <div class="card__body">
      <div class="card__content">
        <div class="card__title"><?php print $title; ?></div>
        <div class="card__summary"><?php print $body; ?></div>
      </div>
    </div>

    <?php
    if ($footer):
      print '<div class="card__footer">' . $footer . '</div>';
    endif
    ?>
  </div>

<?php
if ($href):
  print '</a>';
endif
?>
