<?php

/**
 * @file
 * Fieldable panel pane viewmode template for compact viewmode.
 */
?>

<div <?php print drupal_attributes($attributes_array); ?> class="<?php print implode(" ", $classes_array); ?> compact">

  <div class="<?php if ($image): print 'compact--has-image';
  endif; ?>">
    <div class="compact__wrapper">
      <?php if ($image): ?>
        <div class="compact__media">
          <div class="compact__image">
            <?php print render($image) ?>
          </div>
        </div>
      <?php endif; ?>
      <div class="compact__content" data-equalizer-watch>
        <h3 class="compact__title">
          <?php print $title; ?>
        </h3>
        <?php if ($body): ?>
          <?php print $body; ?>
        <?php endif; ?>
      </div>
    </div>
  </div>

</div>
