<?php

/**
 * @file
 * Template for a 2 column panel layout.
 *
 * This template provides a two column panel display layout, with
 * a wide main column and a smaller side column.
 *
 * Variables:
 * - $id: An optional CSS id to use for the layout.
 * - $content: An array of content, each item in the array is keyed to one
 *   panel of the layout. This layout supports the following sections:
 *   - $content['main']: Content in the main column.
 *   - $content['sidebar']: Content to the right of the main column.
 */
?>

<?php if (!empty($content['top'])): ?>
  <div class="layout-region__top">
    <?php print $content['top']; ?>
  </div>
<?php endif; ?>

<?php if (!empty($content['sidebar'])): ?>
  <div class="row">
    <div class="layout-region__main large-8 large-push-4 columns"><?php print $content['main']; ?></div>
    <div class="layout-region__left large-4 large-pull-8 columns"><?php print $content['sidebar']; ?></div>
  </div>
<?php else: ?>
  <div class="row">
    <div class="layout-region__main large-12 columns"><?php print $content['main']; ?></div>
  </div>
<?php endif; ?>

<?php if (!empty($content['bottom'])): ?>
  <div class="layout-region__bottom">
    <?php print $content['bottom']; ?>
  </div>
<?php endif; ?>
