<?php

/**
 * @file
 * Template for a 2 column panel layout.
 *
 * This template provides a two column panel display layout, with
 * a wide main column and a smaller side column.
 *
 * Variables:
 * - $id: An optional CSS id to use for the layout.
 * - $content: An array of content, each item in the array is keyed to one
 *   panel of the layout. This layout supports the following sections:
 *   - $content['main']: Content in the main column.
 *   - $content['sidebar']: Content to the right of the main column.
 */
?>

<?php if (!empty($content['column_one'])): ?>
  <div class="layout-region__column-one columns medium-4">
    <?php print $content['column_one']; ?>
  </div>
<?php endif; ?>

<?php if (!empty($content['column_two'])): ?>
  <div class="layout-region__column-two columns medium-4 <?php if (empty($content['column_three'])): ?>end<?php
 endif; ?>">
    <?php print $content['column_two']; ?>
  </div>
<?php endif; ?>

<?php if (!empty($content['column_three'])): ?>
  <div class="layout-region__column-three columns medium-4 end">
    <?php print $content['column_three']; ?>
  </div>
<?php endif; ?>
