<?php

/**
 * @file
 * Global theme header include.
 *
 * Available variables:
 * - $site_name: Name of the site for displaying as site title.
 *
 * @see template_preprocess()
 */
?>
<div class="site-header">
  <?php if (!empty($global_menu)): ?>
    <div class="site-header__global-menu">
      <?php print $global_menu; ?>
    </div>
  <?php endif; ?>

  <div class="site-header__content">
    <div class="columns medium-11 large-8">
      <a href="https://www.uq.edu.au/" title="UQ Homepage" class="uq-logo" onclick="ga('send', 'event', 'UQ Header', 'Click', 'Logo: UQ home', '1');">The University of Queensland</a>
      <?php print $site_name; ?>
    </div>

    <?php if (!empty($site_search) && !variable_get('maintenance_mode', 0)): ?>
      <div class="site-header__site-search">
        <?php print $site_search; ?>
      </div>
    <?php endif; ?>

    <?php if (!empty($site_utility)): ?>
      <div class="site-header__site-utility">
        <?php print $site_utility; ?>
      </div>
    <?php endif; ?>
  </div>
</div>
