?><!DOCTYPE html>
<!--[if lt IE 7]> <html class="lt-ie9 lt-ie8 lt-ie7"<?php print $html_attributes ?>> <![endif]-->
<!--[if IE 7]> <html class="lte-ie7 lt-ie9 lt-ie8"<?php print $html_attributes ?>> <![endif]-->
<!--[if IE 8]> <html class="lte-ie8 lt-ie9"<?php print $html_attributes ?>> <![endif]-->
<!--[if lte IE 9]> <html class="lte-ie9"<?php print $html_attributes ?>> <![endif]-->
<!--[if gt IE 9]><!--> <html xmlns="http://www.w3.org/1999/xhtml"<?php print $html_attributes ?>> <!--<![endif]-->
<head>
  <?php print $head; ?>
  <title><?php print $head_title; ?></title>
  <?php print $styles; ?>
  <?php print $scripts; ?>
</head>
<body class="<?php print $classes ?>" <?php print $attributes; ?>>
<div class="skip-link">
  <a href="#main-content" tabindex="1" class="skip-link__link element-invisible element-focusable"><?php print t('Skip to main content'); ?></a>
  <a href="#site-footer" class="skip-link__link element-invisible element-focusable"><?php print t('Skip to footer'); ?></a>
</div>

<?php if ($page['header']): ?>
  <?php print render($page['header']); ?>
<?php endif; ?>

<?php if ($page['navigation']): ?>
  <div class="navigation">
    <?php print render($page['navigation']); ?>
  </div>
<?php endif; ?>

<div class="page">
  <?php if ($messages): ?>
    <div class="page__messages">
      <?php print $messages; ?>
    </div>
  <?php endif; ?>

  <div class="page__header">
    <h1 class="page__title"><?php print $title; ?></h1>
  </div>

  <a id="main-content"></a>
  <div class="page__content">
    <?php print $content; ?>
  </div>

</div>

<?php if ($page['footer']): ?>
  <?php print render($page['footer']); ?>
<?php endif; ?>

</body>
</html>
