<?php

/**
 * @file
 * Default theme implementation to display a 403 error message.
 *
 * Page.tpl.php variables:
 * http://api.drupal.org/api/drupal/modules%21system%21page.tpl.php/7.
 *
 * Available variables:
 * - $base_url: The base URL path of the Drupal installation.
 * - $site_name: The site title.
 * - $logged_in: Whether the user is logged in.
 * - $login_button: Login button markup.
 *
 * @see template_process_denied_message()
 *
 * @ingroup themeable
 */
?>

<div class="columns">
  <?php if ($logged_in): ?>
    <p>You do not have the required permission to view this page.</p>
  <?php else: ?>
    <p>This page requires permission to access. Use your UQ account to log in.</p>

    <br>
    <?php print render($login_button); ?>
    <br><br>

    <p>
      If you have already logged in, click the login button again to continue.
      <a href="https://support.my.uq.edu.au/app/answers/detail/a_id/2535">Trouble logging in?</a>
    </p>
  <?php endif; ?>
</div>
