<?php

/**
 * @file
 * Template for compact viewmode of uq_article content type.
 */
?>
<?php if (!empty($content)): ?>
  <div class="article--compact">
    <div class="article <?php if (!empty($content['field_uq_article_teaser_image'])):
      print 'article--has-image';
    endif; ?>">
      <div class="article__wrapper">
        <?php if (!empty($content['field_uq_article_teaser_image'])): ?>
          <div class="article__media">
            <div class="article__teaser-image">
              <?php print render($content['field_uq_article_teaser_image']) ?>
            </div>
          </div>
        <?php endif; ?>
        <div class="article__content" data-equalizer-watch>
          <h3 class="article__title">
            <a href="<?php print $variables['node_url'] ?>">
              <?php print render($variables['title']) ?>
            </a>
          </h3>
          <?php if (!empty($content['field_uq_publish_date'])): ?>
            <span class="article__publish-date">
              <?php print render($content['field_uq_publish_date']); ?>
            </span>
          <?php endif; ?>
        </div>
      </div>
    </div>
  </div>
<?php endif; ?>
