<?php

/**
 * @file
 * Template for teaser viewmode of uq_article content type.
 */
?>
<?php if (!empty($content)): ?>
<div class="article--teaser">
  <div class="row small-collapse article__wrapper">
    <div class="article <?php if (!empty($content['field_uq_article_teaser_image'])):
      print 'article--has-image';
    endif; ?>">
      <?php if (!empty($content['field_uq_article_teaser_image'])): ?>
      <div class="column small-5 medium-3 article__media">
        <div class="article__teaser-image">
          <?php print render($content['field_uq_article_teaser_image']); ?>
        </div>
      </div>
      <div class="column small-7 medium-9 article__content">
        <?php else: ?>
        <div class="column small-12 article__content">
          <?php endif; ?>
          <h3 class="article__title">
            <a href="<?php print render($variables['node_url']); ?>">
              <?php print render($variables['title']); ?>
            </a>
          </h3>
          <?php if (!empty($content['field_uq_publish_date'])): ?>
            <div class="article__publish-date">
              Published on: <?php print render($content['field_uq_publish_date']); ?>
            </div>
          <?php endif; ?>

          <?php if (!empty($content['field_uq_article_summary'])): ?>
            <div class="article__summary hide-for-small">
              <?php print render($content['field_uq_article_summary']); ?>
            </div>
          <?php endif; ?>
        </div>
      </div>
    </div>
  </div>
  <?php endif; ?>
