<?php

/**
 * @file
 * Template for teaser viewmode of uq_asset content type.
 */
$formatted_date = format_date($node->created, 'custom', 'j M Y');
?>
<?php if (!empty($content)): ?>
  <div class="asset asset--teaser">
    <div class="row">
      <div class="column small-12 asset__content">
        <h3 class="asset__title">
          <a href="<?php print render($variables['node_url']); ?>">
            <?php print render($variables['title']); ?>
          </a>
        </h3>
        <?php if (!empty($content['field_uq_asset_type'])): ?>
          <div class="asset__type">
            <?php print render($content['field_uq_asset_type']); ?>
          </div>
        <?php endif; ?>
        <div class="asset__last-updated">
          Updated: <?php print render($formatted_date); ?>
        </div>
        <?php if (!empty($content['field_uq_asset_description'])): ?>
          <div class="asset__description">
            <?php print render($content['field_uq_asset_description']); ?>
          </div>
        <?php endif; ?>
      </div>
    </div>
  </div>
<?php endif; ?>
