/**
 * @file
 * Template for teaser viewmode of uq_basic_page content type.
 */
?>
<?php if (!empty($content)): ?>
<div class="basic-page basic-page--teaser row <?php if (!empty($content['field_uq_basic_teaser_image'])):
  print 'basic-page--has-image';
endif; ?>">
  <?php if (!empty($content['field_uq_basic_teaser_image'])): ?>
  <div class="column small-5 medium-3 basic-page__media">
    <div class="basic-page__teaser-image">
      <?php print render($content['field_uq_basic_teaser_image']); ?>
    </div>
  </div>
  <div class="column small-7 medium-9 basic-page__content">
    <?php else: ?>
    <div class="column small-12 basic-page__content">
      <?php endif; ?>
      <h3 class="basic-page__title">
        <a href="<?php print render($variables['node_url']); ?>">
          <?php print render($variables['title']); ?>
