<?php

/**
 * @file
 * Template for compact viewmode of uq_blog_post content type.
 */
?>
<?php if (!empty($content)): ?>
  <div class="blog-post--compact">
    <div class="blog-post <?php if (!empty($content['field_uq_blog_teaser_image'])):
      print 'blog-post--has-image';
    endif; ?>">
      <div class="blog-post__wrapper">
        <?php if (!empty($content['field_uq_blog_teaser_image'])): ?>
          <div class="blog-post__media">
            <div class="blog-post__teaser-image">
              <?php print render($content['field_uq_blog_teaser_image']) ?>
            </div>
          </div>
        <?php endif; ?>
        <div class="blog-post__content" data-equalizer-watch>
          <h3 class="blog-post__title">
            <a href="<?php print $variables['node_url'] ?>">
              <?php print render($variables['title']) ?>
            </a>
          </h3>
          <?php if (!empty($content['field_uq_publish_date'])): ?>
            <span class="blog-post__publish-date">
              <?php print render($content['field_uq_publish_date']); ?>
            </span>
          <?php endif; ?>
        </div>
      </div>
    </div>
  </div>
<?php endif; ?>
