<?php

/**
 * @file
 * Template for teaser viewmode of uq_blog_post content type.
 */
?>
<?php if (!empty($content)): ?>
<div class="blog-post blog-post--teaser row <?php if (!empty($content['field_uq_blog_teaser_image'])):
  print 'blog-post--has-image';
endif; ?>">
  <?php if (!empty($content['field_uq_blog_teaser_image'])): ?>
  <div class="column small-5 medium-3 blog-post__media">
    <div class="blog-post__teaser-image">
      <?php print render($content['field_uq_blog_teaser_image']); ?>
    </div>
  </div>
  <div class="column small-7 medium-9 blog-post__content">
    <?php else: ?>
    <div class="column small-12 blog-post__content">
      <?php endif; ?>
      <h3 class="blog-post__title">
        <a href="<?php print render($variables['node_url']); ?>">
          <?php print render($variables['title']); ?>
        </a>
      </h3>
      <?php if (!empty($content['field_uq_publish_date'])): ?>
        <div class="blog-post__publish-date">
          <?php print render($content['field_uq_publish_date']); ?>
        </div>
      <?php endif; ?>
      <?php if (!empty($content['field_uq_blog_summary'])): ?>
        <div class="blog-post__summary hide-for-small">
          <?php print render($content['field_uq_blog_summary']); ?>
        </div>
      <?php endif; ?>
    </div>
  </div>
  <?php endif; ?>
