<?php

/**
 * @file
 * Template for teaser viewmode of uq_profile content type.
 */
$profile_placeholder = url(path_to_theme() . '/images/profile-placeholder.png');
?>

<?php if (!empty($content)): ?>
  <div class="person--teaser">
    <div class="row profile__wrapper">
      <div class="person<?php if (!empty($content['field_uq_profile_photo'])):
        print ' person--has-photo';
      endif; ?>">
        <div class="column small-5 medium-3 profile__media">
          <div class="person__photo">
            <?php if (!empty($content['field_uq_profile_photo'])): ?>
              <?php print render($content['field_uq_profile_photo']); ?>
            <?php else: ?>
              <img src="<?php print $profile_placeholder; ?>"/>
            <?php endif; ?>
          </div>
        </div>
        <div class="column small-7 medium-9 profile__content">
          <h3 class="person__display-name">
            <a href="<?php print render($variables['node_url']); ?>">
              <?php print render($variables['elements']['#node']->title); ?></a>
          </h3>
          <?php if (!empty($content['field_position'])): ?>
            <?php print render($content['field_position']); ?>
          <?php endif; ?>
        </div>
      </div>
    </div>
  </div>
<?php endif; ?>
