<?php

/**
 * @file
 * Template for teaser viewmode of uq_project content type.
 */
?>

<?php if (!empty($content)): ?>
<div class="project--teaser">
  <div class="row project__wrapper">
    <div class="project <?php if (!empty($content['field_uq_project_teaser_image'])):
      print 'project--has-image';
    endif; ?>">
      <?php if (!empty($content['field_uq_project_teaser_image'])): ?>
      <div class="column small-5 medium-3 project__media">
        <div class="project__teaser-image">
          <?php print render($content['field_uq_project_teaser_image']); ?>
        </div>
      </div>
      <div class="column small-7 medium-9 project__content">
        <?php else: ?>
        <div class="column small-12 project__content">
          <?php endif; ?>
          <h3 class="project__title">
            <a href="<?php print render($variables['node_url']); ?>">
              <?php print render($variables['elements']['#node']->title); ?>
            </a>
          </h3>
          <?php if (!empty($content['field_uq_project_start_end_date'])): ?>
            <div class="project__start-end-date">
              <?php print render($content['field_uq_project_start_end_date']); ?>
            </div>
          <?php endif; ?>
          <?php if (!empty($content['field_uq_project_funding_source'])): ?>
            <div class="project__funding-source">
              <?php print render($content['field_uq_project_funding_source']); ?>
            </div>
          <?php endif; ?>
          <?php if (!empty($content['field_uq_project_summary'])): ?>
            <div class="project__summary hide-for-small">
              <?php print render($content['field_uq_project_summary']); ?>
            </div>
          <?php endif; ?>
        </div>
      </div>
    </div>
  </div>
  <?php endif; ?>
