<?php

/**
 * @file
 * Template for teaser viewmode of uq_research_profile content type.
 */
?>
<?php if (!empty($content)): ?>
<div
  class="research-profile research-profile--teaser row <?php if (!empty($content['field_uq_res_profile_teaser_img'])):
    print 'research-profile--has-image';
 endif; ?>">
  <?php if (!empty($content['field_uq_res_profile_teaser_img'])): ?>
  <div class="column small-5 medium-3 research-profile__media">
    <div class="research-profile__teaser-image">
      <?php print render($content['field_uq_res_profile_teaser_img']); ?>
    </div>
  </div>
  <div class="column small-7 medium-9 research-profile__content">
    <?php else: ?>
    <div class="column small-12 research-profile__content">
    <?php endif; ?>
      <h3 class="research-profile__title">
        <a href="<?php print render($variables['node_url']); ?>">
          <?php print render($variables['title']); ?>
        </a>
      </h3>
      <?php if (!empty($content['field_uq_res_profile_intro'])): ?>
        <div class="research-profile__summary hide-for-small">
          <?php print render($content['field_uq_res_profile_intro']); ?>
        </div>
      <?php endif; ?>
    </div>
  </div>
<?php endif; ?>
