/**
 * @file
 * Template for teaser viewmode of uq_structured_page content type.
 */
?>
<?php if (!empty($content)): ?>
<div class="structured-page structured-page--teaser row <?php if (!empty($content['field_uq_structured_teaser_image'])):
  print 'structured-page--has-image';
endif; ?>">
  <?php if (!empty($content['field_uq_structured_teaser_image'])): ?>
  <div class="column small-5 medium-3 structured-page__media">
    <div class="structured-page__teaser-image">
      <?php print render($content['field_uq_structured_teaser_image']); ?>
    </div>
  </div>
  <div class="column small-7 medium-9 structured-page__content">
    <?php else: ?>
    <div class="column small-12 structured-page__content">
      <?php endif; ?>
      <h3 class="structured-page__title">
        <a href="<?php print render($variables['node_url']); ?>">
          <?php print render($variables['title']); ?>
        </a>
      </h3>
      <?php if (!empty($content['field_uq_structured_summary'])): ?>
        <div class="structured-page__intro hide-for-small">
          <?php print render($content['field_uq_structured_summary']); ?>
        </div>
      <?php endif; ?>
