<?php

/**
 * @file
 * Template for compact viewmode of uq_student_story content type.
 */
?>
<?php if (!empty($content)): ?>
  <div class="student-story--compact">
    <div class="student-story <?php if (!empty($content['field_uq_student_story_photo'])):
      print 'student-story--has-image';
    endif; ?>">
      <div class="student-story__wrapper">
        <?php if (!empty($content['field_uq_student_story_photo'])): ?>
          <div class="student-story__media">
            <div class="student-story__photo">
              <?php print render($content['field_uq_student_story_photo']) ?>
            </div>
          </div>
        <?php endif; ?>
        <div class="student-story__content" data-equalizer-watch>
          <h3 class="student-story__title">
            <a href="<?php print $variables['node_url'] ?>">
              <?php print render($variables['title']) ?>
            </a>
          </h3>
          <?php if (!empty($content['field_uq_student_story_quote'])): ?>
            <span class="student-story__quote">
              <?php print render($content['field_uq_student_story_quote']); ?>
            </span>
          <?php endif; ?>
        </div>
      </div>
    </div>
  </div>
<?php endif; ?>
