 */
?>
<?php if (!empty($content)): ?>
<div class="student-story student-story--teaser row <?php if (!empty($content['field_uq_student_story_photo'])):
  print 'student-story--has-image';
endif; ?>">
  <?php if (!empty($content['field_uq_student_story_photo'])): ?>
  <div class="column small-5 medium-3 student-story__media">
    <div class="student-story__teaser-image">
      <?php print render($content['field_uq_student_story_photo']); ?>
    </div>
  </div>
  <div class="column small-7 medium-9 student-story__content">
    <?php else: ?>
