<?php

/**
 * @file
 * Template for teaser viewmode of uq_team content type.
 */
?>
<?php if (!empty($content)): ?>
  <div class="team team--teaser">
    <div class="row">
      <div class="column small-12 team__content">
        <h3 class="team__title">
          <a href="<?php print render($variables['node_url']); ?>">
            <?php print render($variables['title']); ?>
          </a>
        </h3>
        <?php if (!empty($content['field_uq_team_phone']['#items'][0]['value'])): ?>
          <div class="team__phone">
            <span class="icon icon-phone icon--prefix" title="Phone"></span> <a
              href="tel:<?php print render($content['field_uq_team_phone']); ?>"><?php print render($content['field_uq_team_phone']); ?></a>
          </div>
        <?php endif; ?>
        <?php if (!empty($content['field_uq_team_email']['#items'][0]['email'])): ?>
          <div class="team__email">
          <span class="icon icon-envelope icon--prefix"
                title="Email"></span> <?php print render($content['field_uq_team_email']); ?>
          </div>
        <?php endif; ?>
      </div>
    </div>
  </div>
<?php endif; ?>
