<?php

/**
 * @file
 * Default theme implementation for a single paragraph item.
 *
 * Available variables:
 * - $content: An array of content items. Use render($content) to print them
 *   all, or print a subset such as render($content['field_example']). Use
 *   hide($content['field_example']) to temporarily suppress the printing of a
 *   given element.
 * - $classes: String of classes that can be used to style contextually through
 *   CSS. It can be manipulated through the variable $classes_array from
 *   preprocess functions. By default the following classes are available, where
 *   the parts enclosed by {} are replaced by the appropriate values:
 *   - entity
 *   - entity-paragraphs-item
 *   - paragraphs-item-{bundle}
 *
 * Other variables:
 * - $classes_array: Array of html class attribute values. It is flattened
 *   into a string within the variable $classes.
 *
 * @see template_preprocess()
 * @see template_preprocess_entity()
 * @see template_process()
 */
?>
<?php if (!empty($content)): ?>
  <div class="<?php print $classes; ?>"<?php print $attributes; ?>>
    <div class="content"<?php print $content_attributes; ?>>
      <?php
      if ($title_suffix):
        print render($title_suffix);
      endif;
      ?>
      <?php
      if (isset($content['field_members_description'])):
        print render($content['field_members_description']);
      endif;
      ?>
      <?php
      if (!empty($content['field_members'])): ?>
        <ul class='medium-block-grid-2'>
          <?php foreach ($content['field_members'] as $key => $item) { ?>
            <?php if (is_numeric($key)): ?>
              <li>
                <?php print render($content['field_members'][$key]); ?>
              </li>
            <?php endif; ?>
          <?php } ?>
        </ul>
      <?php endif; ?>
    </div>
  </div>
<?php endif; ?>
