<?php

/**
 * @file
 * Global site search form for including in global site header.
 *
 * Available variables:
 * - $search_url: Path to search results page.
 * - $as_sitesearch: URL of site being searched. Used to filter results to
 * just the current site.
 *
 * @see template_preprocess()
 */
?>
<form class="search__form site-search__form" method="get" action="<?php print $search_url; ?>">
  <fieldset class="site-search__fieldset">
    <legend class="site-search__legend">Site search</legend>
    <label for="site-search__query" class="search__title site-search__title">Search</label>
    <input id="site-search__query"
           class="site-search__input site-search__query"
           type="text"
           value="Search this site"
           name="q"
           placeholder="Search this site"
           onfocus="if (this.value === this.placeholder) {this.value = '';}"
           onblur="if (this.value === '') {this.value = this.placeholder;}" />
    <input name="submit" value="&#xf002;" class="site-search__input site-search__submit" title="Search this site" type="submit" />
    <input type="hidden" name="output" value="xml_no_dtd" />
    <input type="hidden" name="client" value="ws" />
    <input type="hidden" name="proxystylesheet" value="ws" />
    <input type="hidden" name="as_sitesearch" value="<?php print $as_sitesearch; ?>" />
  </fieldset>
</form>
